import React, { useState } from 'react';
import { FormattedMessage } from 'react-intl';
import InputBase from '@material-ui/core/InputBase';
import { Helmet } from 'react-helmet';
import {
  Container,
  Row,
  Col,
  Label,
  Select,
  MenuItem,
  Input,
  TabContent,
  TabPane,
  Nav,
  NavItem,
  NavLink,
} from 'reactstrap';
import { BiUserCircle, BiLock } from 'react-icons/bi';
import { RiKeyLine } from 'react-icons/ri';
import classnames from 'classnames';
import messages from './messages';
import Wrapper from './Wrapper';
import Img from '../../components/Img';
import BasicInfo from '../../components/MyProfilePage/BasicInfo';
import ChangePassword from '../../components/MyProfilePage/ChangePassword';
import TwoFAAuthentication from '../../components/MyProfilePage/TwoFAAuthentication';
// import Profile from '../../images/profile.jpg';

const BootstrapInput = withStyles(theme => ({
  root: {
    'label + &': {
      marginTop: 0,
    },
  },
  input: {
    borderRadius: 4,
    position: 'relative',
    backgroundColor: theme.palette.background.paper,
    border: '1px solid #e6e6e6',
    fontSize: 14,
    color: '#484848',
    padding: '10px 26px 10px 12px',
    transition: theme.transitions.create(['border-color', 'box-shadow']),
    // Use the system font instead of the default Lato font.
    fontFamily: ['Lato', 'sans-serif'].join(','),
    '&:focus': {
      borderRadius: 4,
    },
  },
}))(InputBase);

export default function MyProfilePage() {
  const [activeTab, setActiveTab] = useState('1');

  const toggle = tab => {
    if (activeTab !== tab) setActiveTab(tab);
  };
  return (
    <div className="sub_page">
      <Helmet>
        <title>My Profile</title>
        <meta name="description" content="My Profile Page" />
      </Helmet>
      <Wrapper id="list">
        <Container fluid>
          <Row>
            <Col lg={4} md={4} sm={12} xs={12}>
              <div className="profile_sidebar">
                <div className="pro_img">
                  <div className="inner">
                    <Img
                      src="data:image/png;base64,"
                      alt="Profile"
                    />
                  </div>
                  <Label>
                    {' '}
                    <FormattedMessage {...messages.ChangeProfile} />
                    <Input type="file" />
                  </Label>
                </div>
                <Nav tabs>
                  <NavItem>
                    <NavLink
                      className={classnames({ active: activeTab === '1' })}
                      onClick={() => {
                        toggle('1');
                      }}
                    >
                      <BiUserCircle />{' '}
                      <FormattedMessage {...messages.EditProfile} />
                    </NavLink>
                  </NavItem>
                  <NavItem>
                    <NavLink
                      className={classnames({ active: activeTab === '2' })}
                      onClick={() => {
                        toggle('2');
                      }}
                    >
                      <BiLock />{' '}
                      <FormattedMessage {...messages.ChangePassword} />
                    </NavLink>
                  </NavItem>
                  <NavItem>
                    <NavLink
                      className={classnames({ active: activeTab === '3' })}
                      onClick={() => {
                        toggle('3');
                      }}
                    >
                      <RiKeyLine /> <FormattedMessage {...messages.TwoFAuth} />
                    </NavLink>
                  </NavItem>
                </Nav>
              </div>
            </Col>
            <Col lg={8} md={8} sm={12} xs={12}>
              <TabContent activeTab={activeTab}>
                <TabPane tabId="1">
                  <BasicInfo />
                </TabPane>
                <TabPane tabId="2">
                  <ChangePassword />
                </TabPane>
                <TabPane tabId="3">
                  <TwoFAAuthentication active={activeTab} />
                </TabPane>
              </TabContent>
            </Col>
          </Row>
        </Container>
      </Wrapper>
    </div>
  );
}
